window.BookDetailView=Backbone.View.extend({initialize:function(){this.template=_.template(template.get("book-detail"))},events:{},showBook:function(t){$(".book__title").html(t.title),$(".book__author").html(t.authors.join()),$(".book__img").attr("src",t.imageLinks.thumbnail.replace("zoom=1","zoom=2")),$(".book__publisher").html(t.publisher),$(".book__pub-date").html(t.publishedDate),$(".book__page-count").html(t.pageCount+" pages"),$(".book__description").html(t.description),$(".book__preview a").attr("href",t.previewLink)},render:function(){return this.$el.html(this.template()),this.el}});